INSERT INTO AT_HolidayGroup (holidayGroupCd, holidayGroupNm, holidayGroupDescription, sysSiyouKbn, kousinsyaId, kousinTime, kousinKinou, version) VALUES ('JapanHolidays', '祝日法で定める休日', '日本の祝日法（国民の祝日に関する法律）で定められた国民の祝日・国民の休日を休日と見なす休日体系です。土曜日・日曜日は休日と見なしません。また、明治・大正時代の祝日も含まれません。', '1', 'init', '0', 'init', '0');
INSERT INTO AT_HolidayGroup (holidayGroupCd, holidayGroupNm, holidayGroupDescription, sysSiyouKbn, kousinsyaId, kousinTime, kousinKinou, version) VALUES ('CalendarHolidays', 'カレンダー休日', '日本の祝日法の休日と日曜日を休日と見なす休日体系です。土曜日は休日と見なしません。', '1', 'init', '0', 'init', '0');
INSERT INTO AT_HolidayGroup (holidayGroupCd, holidayGroupNm, holidayGroupDescription, sysSiyouKbn, kousinsyaId, kousinTime, kousinKinou, version) VALUES ('WeekendHolidays', '土日祝日', '日本の祝日法の休日と土曜日・日曜日を休日と見なす休日体系です。', '1', 'init', '0', 'init', '0');
INSERT INTO AT_HolidayGroup (holidayGroupCd, holidayGroupNm, holidayGroupDescription, sysSiyouKbn, kousinsyaId, kousinTime, kousinKinou, version) VALUES ('BankHolidays', '金融機関休日', '金融機関の休日体系です。「銀行法（昭和五十六年法律第五十九号）第十五条第一項」並び「銀行法施行令（昭和五十七年政令第四十号）第五条」に従い、土・日・祝日・12月31日～1月3日を休日と見なします。', '1', 'init', '0', 'init', '0');
